mapper.select:
select que_id
from forum_question
where course_id = ${c_id}
order by up_date
limit #{index},#{page_size}

select q.que_id qId, q.course_id cId, q.`name` qName, q.context, q.up_date qDate, a.id aId, a.que_id aQId, a.`name` aName, # a.answer,
LEFT(a.answer,20) answer, a.up_date aDate from forum_answer a RIGHT JOIN forum_question q ON q.que_id = a.que_id where q.course_id = #{c_id} and q.que_id =#{q_id} ORDER BY q.up_date LIMIT 0,1
a.answer, a.up_date aDate from forum_answer a RIGHT JOIN forum_question q ON q.que_id = a.que_id where q.course_id = #{c_id} and q.que_id =#{q_id} ORDER BY q.up_date LIMIT ${index_answer},${answer_page_size}
